package aula7;import java.util.Date;import java.text.SimpleDateFormat;public class Aula7 {public static void main(String[]args){System.out.println(printData());}public static String printData() {SimpleDateFormat formato = new SimpleDateFormat("'Meu colega o dia de hoje é:' dd MMMM 'de' y 'e são extamente' HH 'horas e' mm 'minutos' 'e' s 'segundos e aproximadamente' S 'Milesegundos EXATAMENTE.'");Date data = new Date();return formato.format(data);}}